<template>
	<view>
		<view class="content lighter-icon">
			<view class="list">
				<view class="row" v-for="(row,index) in addressList" :key="index" @tap="select(row)">
					<view class="center">
						<view class="name-tel">
							<view class="name">{{row.address_realname}}</view>
							<view class="tel">{{row.address_mobile}}</view>
							<view class="default background-text" v-if="row.address_is_default===1">
								默认
							</view>
						</view>
						<view class="address" @tap.stop="edit(row.address_id)">	{{row.address_provience|getAreaName}} - {{row.address_area|getAreaName}} - {{row.address_county|getAreaName}} - {{row.address_detail}}
						</view>
					</view>
					<view class="right">
						<text class="iconfont icon-arrow-right">
						</text>
					</view>
					<image class="a-bg" src="data:image/png;base64,"></image>
				</view>
			</view>
		</view>
		<view class="add">
			<view class="btn background-text" @tap="add">
				<view class="icon tianjia">+</view>新增地址
			</view>
		</view>
	</view>
</template>
<script>
	export default {
		data() {
			return {
				isSelect: false,
				addressList: [],
				userinfo: uni.getStorageSync('userInfo').memberinfo_account,
				data: null,
				is_order:''
			};
		},
		onShow() {
			this.getAddress()
		},
		onLoad(e) {
			if (e.type == 'select') {
				this.isSelect = true;
			}else if(e.type==='order'){
				this.is_order = 'order'
			}
		},
		mounted() {},
		methods: {
			// 修改地址
			edit(id) {
				let url = "/center/editAddress/EditAddress?id=" + id
				uni.navigateTo({
					url: this.is_order?url+'&is_order='+this.is_order:url
				})
			},
			// 添加地址
			add() {
				let url = "/center/editAddress/EditAddress?type=add"
				uni.navigateTo({
					url: this.is_order?url+'&is_order='+this.is_order:url
				})
			},
			select(row) {
				//是否需要返回地址(从订单确认页跳过来选收货地址)
				if (!this.isSelect) {
					return;
				}
				uni.setStorage({
					key: 'selectAddress',
					data: row,
					success() {
						uni.navigateBack();
					}
				})
			},
			// 获取地址
			async getAddress() {
				let data = {
					'address_account': this.userinfo
				}
				await this.$api.getAddresses(data).then(res => {
					this.addressList = res.data;
				})
			}
		}
	}
</script>

